DROP DATABASE IF EXISTS Matrix;
CREATE DATABASE Matrix;
USE Matrix;

drop table IF EXISTS Consultant;
create table Consultant(
    ConsultantId int NOT NULL AUTO_INCREMENT PRIMARY KEY,
    Nom varchar(150) NOT NULL,
	Prenom varchar(150),
	Commentaire varchar(300),
    email varchar(320) DEFAULT NULL,
	pswd char(40),
    must_change_password bit default 1,
	CONSTRAINT UQ_Email UNIQUE  (email)
);

drop table IF EXISTS CompetenceType;
create table CompetenceType(
    CompetenceTypeId nvarchar(10) NOT NULL PRIMARY KEY,	
    CompetenceTypeDescription varchar(150) NOT NULL,
	DisplaySequence int
