drop table IF EXISTS Competence;
create table Competence(
    CompetenceId nvarchar(10) NOT NULL PRIMARY KEY,	
    ParentId nvarchar(10),
	CompetenceTypeId nvarchar(10) NOT NULL,
    CompetenceDescription varchar(150) NOT NULL,	
	CONSTRAINT UQ_Competence UNIQUE(CompetenceId, CompetenceDescription),
    CONSTRAINT FOREIGN KEY(ParentId) REFERENCES Competence(CompetenceId),
	CONSTRAINT FOREIGN KEY(CompetenceTypeId) REFERENCES CompetenceType(CompetenceTypeId)
);

drop table IF EXISTS Niveau;
create table Niveau(
    NiveauId nvarchar(10) NOT NULL,
    NiveauNom varchar(150) NOT NULL,
	CompetenceTypeId nvarchar(10),
    NiveauDescription varchar(300) NULL,
    CONSTRAINT PK_Niveau PRIMARY KEY (NiveauId,CompetenceTypeId),
	CONSTRAINT FK_CompetenceType FOREIGN KEY(CompetenceTypeId) REFERENCES CompetenceType(CompetenceTypeId)
);

drop table IF EXISTS Consultant_Competence;
create table Consultant_Competence(
    ConsultantId int,
    CompetenceId nvarchar(10),
    ParentId nvarchar(10),
	NiveauId nvarchar(10),
	NiveauNom nvarchar(10),
    CONSTRAINT PK_Consultant_Competence PRIMARY KEY (ConsultantId,CompetenceId),
	CONSTRAINT FOREIGN KEY(ConsultantId) REFERENCES Consultant(ConsultantId),
	CONSTRAINT FOREIGN KEY(CompetenceId) REFERENCES Competence(CompetenceId),
	CONSTRAINT FOREIGN KEY(ParentId) REFERENCES Competence(CompetenceId)
);

