drop function if exists validate_password
DELIMITER $$

CREATE FUNCTION validate_password(
	in_email varchar(320),
    in_password varchar(255)
) 
RETURNS boolean
DETERMINISTIC
BEGIN
    DECLARE validate_password boolean default false;

    IF exists(select * from Consultant where email = in_email and aes_decrypt(pswd)=in_password) THEN
		SET validate_password = true;
    END IF;
	RETURN (validate_password);
END$$
DELIMITER ;


