drop function if exists encrypt_password
DELIMITER $$

CREATE FUNCTION encrypt_password(
    in_password varchar(255)
) 
RETURNS char(40)
DETERMINISTIC
BEGIN
	RETURN (sha1(in_password));
END$$
DELIMITER ;


