drop PROCEDURE if exists create_consultant
DELIMITER $$
CREATE PROCEDURE create_consultant(
 in inName varchar(150),
 in inPrenom varchar(150),
 in inEmail varchar(320),
 in inPassword varchar(255),
 out outConsultantId int
)
BEGIN
	insert Consultant(Nom, Prenom, Email, must_change_password, pswd)
	values (inName, inPrenom, inEmail, 0, encrypt_password(inPassword));

	set outConsultantId= @@identity;
END$$

DELIMITER ;

