drop PROCEDURE if exists change_password
DELIMITER $$
CREATE PROCEDURE change_password(
 in inEmail varchar(320),
 in inOldPassword varchar(255),
 in inNewPassword varchar(255),
 out outPasswordChanged boolean
)
BEGIN
update Consultant
set pswd = sha1(inNewPassword), 
	must_change_password=0
where email = inEmail and
	pswd = sha1(inOldPassword);
    
set outPasswordChanged = (row_count() = 1);
END$$

DELIMITER ;